// src/screens/StaffListScreen.js
import React, { useEffect } from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';
import { Button } from 'react-native-paper';
import useStaffService from '../services/StaffService';

const StaffListScreen = ({ navigation }) => {
  const { getStaffList } = useStaffService();
  const staffList = getStaffList();

  useEffect(() => {
    // Fetch data from local storage or API if needed
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Staff List Screen</Text>
      <FlatList
        data={staffList}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.listItem}>
            <Text>{item.name}</Text>
          </View>
        )}
      />
      <Button mode="contained" onPress={() => navigation.navigate('StaffDetails')}>
        View Staff Details
      </Button>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 20,
    marginBottom: 20,
  },
  listItem: {
    padding: 10,
    borderBottomWidth: 1,
    borderColor: '#ccc',
  },
});

export default StaffListScreen;
