// screens/StaffListScreen.js
import React from 'react';
import { View, Text, FlatList, StyleSheet,Image } from 'react-native';
import { List } from 'react-native-paper';
 // Example of a relative path
 
 

const StaffListScreen = ({ navigation }) => {
 
  const staffData = [
    {
      id: 1,
      name: 'John Smith',
      phoneNumber: '02 9988 2211',
      address: {
        street: '1 Code Lane',
        city: 'Javaville',
        state: 'NSW',
        postalCode: '0100',
        country: 'Australia',
      },
    },
    {
      id: 2,
      name: 'Sue White',
      phoneNumber: '03 8899 2255',
      address: {
        street: '16 Bit Way',
        city: 'Byte Cove',
        state: 'QLD',
        postalCode: '1101',
        country: 'Australia',
      },
    },
    {
      id: 3,
      name: 'Bob O’Bits',
      phoneNumber: '05 7788 2255',
      address: {
        street: '8 Silicon Road',
        city: 'Cloud Hills',
        state: 'VIC',
        postalCode: '1001',
        country: 'Australia',
      },
    },
    {
      id: 4,
      name: 'Mary Blue',
      phoneNumber: '06 4455 9988',
      address: {
        street: '4 Processor Boulevard',
        city: 'Appletson',
        state: 'NT',
        postalCode: '1010',
        country: 'Australia',
      },
    },
    {
      id: 5,
      name: 'Mick Green',
      phoneNumber: '02 9988 1122',
      address: {
        street: '700 Bandwidth Street',
        city: 'Bufferland',
        state: 'NSW',
        postalCode: '0110',
        country: 'Australia',
      },
    },
    // Add more staff profiles as needed
  ];
  
  

   

  return (
    <View style={styles.container}>

       
      <FlatList
        data={staffData}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <List.Item
            title={item.name}
            onPress={() => navigation.navigate('StaffDetails', { staff: item })}
          />
        )}
      />
     {/* First Row */}
     <View style={styles.row}>
        <Text style={styles.text}>  <Image 
        source={require('../Icons/roi-image-resources/roi-logo.jpg')}
         
        style={styles.logo}
        resizeMode="contain"
      />
      <Text style={styles.title}>ROI Staff Lists</Text>
  </Text>
        <Text style={styles.text}>Item 2</Text>
      </View>

      {/* Second Row */}
      <View style={styles.row}>
        <Text style={styles.text}>Item 3</Text>
        <Text style={styles.text}>Item 4</Text>
      </View>

      {/* Third Row */}
      <View style={styles.row}>
        <Text style={styles.text}>Item 5</Text>
        <Text style={styles.text}>Item 6</Text>
      </View>

    </View>
  );
};

const styles = StyleSheet.create({
    container: {
    flex: 1,
    padding: 16,
  
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
    flexDirection: 'row'
  }, logo: {
    width: 50,
    height: 60,
    flexDirection: 'row'
  },  row: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  text: {
    padding: 10,
   
    marginRight: 10,
    flexDirection: 'row', // Align children horizontally
    alignItems: 'center', // Center children vertically
  },
});

export default StaffListScreen;
