// screens/StaffDetailsScreen.js
import React from 'react';
import { View, Text ,Image,Button } from 'react-native';

const StaffDetailsScreen = ({ route , navigation}) => {
  const { staff } = route.params;

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.name}</Text>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.phoneNumber}</Text>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.address.street }</Text>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.address.city }</Text>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.address.state }</Text>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.address.postalCode }</Text>
      <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{staff.address.country }</Text>
       
      {/* Display additional details as needed */}

      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>

      <Button
        title="Update"
        onPress={() => navigation.navigate('AddUpdateStaffScreen')}
      />
    </View>


    </View>
  );
};

export default StaffDetailsScreen;
