// screens/StaffDetailsScreen.js
import React from "react";
import { View, Text, Image, Button ,StyleSheet} from "react-native";

const StaffDetailsScreen = ({ route, navigation }) => {
  const { staff } = route.params;

  return (
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <view style={styles.row}>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>{staff.name}</Text>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>
          {staff.phoneNumber}
        </Text>
      </view>
      <view style={styles.row}>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>
          {staff.address.street}
        </Text>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>
          {staff.address.city}
        </Text>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>
          {staff.address.state}
        </Text>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>
          {staff.address.postalCode}
        </Text>
        <Text style={{ fontSize: 20, fontWeight: "bold" }}>
          {staff.address.country}
        </Text>

        {/* Display additional details as needed */}
      </view>
      
        <View
          style={{ flex: 1, justifyContent: "center", alignItems: "center" }}
        >
          <Button
            title="Update"
            onPress={() => navigation.navigate("AddUpdateStaffScreen")}
          />
        </View>
     
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 16,
    flexDirection: "row",
  },
  row: {
    flexDirection: "row",
    marginBottom: 10,
  },
  text: {
    padding: 10,

    marginRight: 10,
  },
});

export default StaffDetailsScreen;
