// screens/StaffListScreen.js
import React, { useState, useEffect } from "react";
import { View, Text, FlatList, StyleSheet, Image } from "react-native";
import { List } from "react-native-paper";
// Example of a relative path
import MyIconButton from '../Icons/roi-image-resources/icon-pack/plus-solid.svg';

const StaffListScreen = ({ route, navigation }) => {
  const { staff } = route;
  const [staffData, setStaffData] = useState([
    {
      id: 1,
      name: "John Smith",
      phoneNumber: "02 9988 2211",
      address: {
        street: "1 Code Lane",
        city: "Javaville",
        state: "NSW",
        postalCode: "0100",
        country: "Australia",
      },
    },
    {
      id: 2,
      name: "Sue White",
      phoneNumber: "03 8899 2255",
      address: {
        street: "16 Bit Way",
        city: "Byte Cove",
        state: "QLD",
        postalCode: "1101",
        country: "Australia",
      },
    },
    {
      id: 3,
      name: "Bob O’Bits",
      phoneNumber: "05 7788 2255",
      address: {
        street: "8 Silicon Road",
        city: "Cloud Hills",
        state: "VIC",
        postalCode: "1001",
        country: "Australia",
      },
    },
    {
      id: 4,
      name: "Mary Blue",
      phoneNumber: "06 4455 9988",
      address: {
        street: "4 Processor Boulevard",
        city: "Appletson",
        state: "NT",
        postalCode: "1010",
        country: "Australia",
      },
    },
    {
      id: 5,
      name: "Mick Green",
      phoneNumber: "02 9988 1122",
      address: {
        street: "700 Bandwidth Street",
        city: "Bufferland",
        state: "NSW",
        postalCode: "0110",
        country: "Australia",
      },
    },
    // Add more staff profiles as needed
  ]);


  const addNewStaff = () => {
    // Generate a new unique id for the new staff member
    const newStaffId = staffData.length + 1;

    // Create the new staff member object
    const newStaffMember = {
      id: newStaffId,
      name: "df d",
      phoneNumber: "02 2343 2211",
      address: {
        street: "1 Code Lane",
        city: "23423r3fdsd",
        state: "sdf",
        postalCode: "434",
        country: "sdgswrh",
      }
    };


 
    debugger
    
    staffData = [...staffData, newStaffId]

  };


  if (route.params != null) {
    debugger;

    const updateStaffById = ( updatedData) => {
      debugger;
      staffData;
      // const staffWithIdOne = staffData.find((staff) => staff.id === 1);

      const staffIndex = staffData.findIndex((staff) => staff.id === updatedData);
debugger
      if (staffIndex !== -1) {
        
        staffData[staffIndex].name = updatedData.name;
        staffData[staffIndex].phoneNumber = updatedData.phoneNumber;
        staffData[staffIndex].address.street = updatedData.street;
        staffData[staffIndex].address.city = updatedData.city;
        staffData[staffIndex].address.state = updatedData.state;
        staffData[staffIndex].address.postalCode = updatedData.postalCode;
        staffData[staffIndex].address.country = updatedData.country;
      }
      else{
        
        debugger
        addNewStaff();
      

      }
    };
    updateStaffById(
      { id: route.params.newStaff.id ,
       name: route.params.newStaff.name  ,
       phoneNumber: route.params.newStaff.phoneNumber ,
       street: route.params.newStaff.street ,
       city: route.params.newStaff.city ,
       state: route.params.newStaff.state ,
       postalCode: route.params.newStaff.postalcode ,
       country: route.params.newStaff.country }
    );
    debugger;
    route.params = null;
  }

  return (
    <View style={styles.container}>
      {/* First Row */}
      <View style={styles.row}>
        <Text style={styles.text}>
          {" "}
          <Image
            source={require("../Icons/roi-image-resources/roi-logo.jpg")}
            style={styles.logo}
            resizeMode="contain"
          />
          {/* <Text style={styles.title}>ROI Staff Employees</Text> */}
        </Text>
      </View>

      {/* Second Row */}
      <View style={styles.row}>
        <FlatList
          data={staffData}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <List.Item
              title={item.name}
              onPress={() =>
                navigation.navigate("StaffDetails", { staff: item })
              }
            />
          )}
        />
        {/* <Text style={styles.text}>Item 4</Text> */}
      </View>
    {/* <View style={styles.row}>
    <MyIconButton      onPress={() =>
            navigation.navigate("AddUpdateStaffScreen" )
          } />
    </View> */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 16,
    flexDirection: "row",
  },
  logo: {
    width: 50,
    height: 60,
    flexDirection: "row",
  },
  row: {
    flexDirection: "row",
    marginBottom: 10,
  },
  text: {
    padding: 10,

    marginRight: 10,
  },
});

export default StaffListScreen;
