import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { List } from 'react-native-paper';

const StaffListScreen = ({ navigation }) => {
  const [staffList, setStaffList] = useState([
    { id: 1, name: 'John Doe' },
    { id: 2, name: 'Jane Doe' },
    // Add more staff profiles as needed
  ]);

  useEffect(() => {
    // Fetch staff profiles from the backend API or local storage
    // Update the staffList using setStaffList
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Staff List</Text>
      <FlatList
        data={staffList}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => navigation.navigate('StaffDetails', { staff: item })}>
            <List.Item title={item.name} />
          </TouchableOpacity>
        )}
      />
      <TouchableOpacity onPress={() => navigation.navigate('AddUpdateStaff')}>
        <List.Item title="Add Staff" left={(props) => <List.Icon {...props} icon="plus" />} />
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  title: {
    fontSize: 24,
    marginBottom: 16,
  },
});

export default StaffListScreen;
